import{j as a,A as n,a4 as s}from"./i8clt0mfz495j7ii.js";import{P as r}from"./pdoi0jg7esbcecz5.js";import"./ghewn7l2ynbm32fm.js";import"./b01hwr627par42jb.js";import"./o1s10g7vrpyumh3e.js";import"./j0yo6uv6652vva62.js";import"./k7l1y4e4hdi0wwbb.js";import"./mcxstkwqbnk0lxek.js";import"./l1wculq9jxypsgqa.js";const i={slug:"use-cases/university-educators",audience:"professors",title:"Chats for\nuniversity educators",description:"Faculty from a dozen disciplines shared chats they use for teaching and research. \n\n Tap a chat to get started.",template:"default",shareCopyText:"Chats for university educators | ChatGPT",overrides:{titleMaxWidth:600},prompts_with_categories:[{category:{name:"All",slug:"",color:"",assets:[{url:"https://cdn.openai.com/chatgpt/ctf-cdn/professors/all_1.png",alt:"Image for professors prompts 1"},{url:"https://cdn.openai.com/chatgpt/ctf-cdn/professors/all_2.png",alt:"Image for professors prompts 2"},{url:"https://cdn.openai.com/chatgpt/ctf-cdn/professors/all_3.png",alt:"Image for professors prompts 3"}],prompts:[{number:1,title:"Refine your syllabus",prompt:"Please analyze this syllabus carefully, taking on the perspective of an experienced educator. Your analysis should cover several specific areas of consideration. For each section, please be thorough but concise, focusing on actionable feedback.\n\nPlease cover:\n• First Impressions & Questions\n• Content Gaps Analysis\n• Suggested Additional Sections\n• Visual Enhancement Opportunities\n• General Improvement Ideas",promptQuery:"Please analyze this syllabus carefully, taking on the perspective of an experienced educator. Your analysis should cover several specific areas of consideration. For each section, please be thorough but concise, focusing on actionable feedback.\n\nPlease cover:\n• First Impressions & Questions\n• Content Gaps Analysis\n• Suggested Additional Sections\n• Visual Enhancement Opportunities\n• General Improvement Ideas\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Syllabus.pdf"}]},{number:2,title:"Generate a quiz based on your lecture",prompt:"Generate 10 multiple choice questions I can use as a quiz during this lecture to help my students test their understanding of the material. For each question, indicate the correct answer with an explanation.",promptQuery:"Generate 10 multiple choice questions I can use as a quiz during this lecture to help my students test their understanding of the material. For each question, indicate the correct answer with an explanation.\n\n[Upload docs]",attachments:[{icon:"slide",fileName:"Slides.ppt"},{icon:"file",fileName:"Lecture transcript.txt"}]},{number:3,title:"Process your course feedback",prompt:"Analyze these course evaluations I received and take apart the feedback so that I can make it actionable. I'm interested in their overall trends and the things that point to improvements I can make. Rank these improvements by importance and explain why.",promptQuery:"Analyze these course evaluations I received and take apart the feedback so that I can make it actionable. I'm interested in their overall trends and the things that point to improvements I can make. Rank these improvements by importance and explain why.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Course evaluations.pdf"}]},{number:4,title:"Tailor a problem set to your student’s interests",prompt:"Here’s a set of practice problems on linear equations. Keep the core math problems and number values the same, but rewrite them for a student interested in skateboarding, anime and basketball.",promptQuery:"Here’s a set of practice problems on [topic]. Keep the core math problems and number values the same, but rewrite them for a student interested in [personal interests].\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Practice problems.pdf"}]},{number:5,title:"Brainstorm hands-on activities",prompt:"Generate 5 unconventional, surprising, creative activities for a graduate-level journalism class on presentation skills. Activities should help students:\n• Practice 30-sec venture pitches\n• Master calming breathing techniques\n• Apply our presentation principles (eye contact, charisma, gentle hands, planted feet, simple slides, clear start/end)\n\nFocus on surprising approaches that reinforce these principles while building students' confidence. The class is 60 minutes.",promptQuery:"Generate 5 unconventional, surprising, creative activities for a [level] class on [topic]. Activities should help students [learning goals]. Focus on surprising approaches that reinforce these principles while building students' confidence. The class is [length] minutes.",attachments:[]},{number:6,title:"Improve your lectures",prompt:"Help me identify what I could have done better in this lecture for next time. I am teaching an introductory software engineering course and the lecture is on the impact of software engineering on the industry. Please provide a list of concrete improvements I could make.",promptQuery:"Help me identify what I could have done better in this lecture for next time. I am teaching [course]. Lecture is on [topic]. Please provide a list of concrete improvements I could make.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Syllabus.pdf"},{icon:"slide",fileName:"Slides.ppt"},{icon:"file",fileName:"Lecture transcript.txt"}]},{number:7,title:"Find new resources",prompt:"I am teaching a course titled “Thinking Analytically in an Uncertain World” for students who are doing a master’s in public policy. I would like to open the course with the example of the decision that President Kennedy and his administration made during the Cuban Missile Crisis. Recommend resources I should read or watch to prepare.",promptQuery:"I’m preparing for my course on [topic]. Recommend resources I should read or watch.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Syllabus.pdf"}]},{number:8,title:"Make images for your slides",prompt:"I have a framework that I like to share with my entrepreneurship and tech innovation students about the impact of pivots that draws an analogy to the planets in our solar system. See my slides.\n\nHelp me express all of this in an image I can show my students.",promptQuery:"I’m giving a lecture on [topic]. I want to visualize [concept]. Help me express this in an image I can show my students.",attachments:[{icon:"slide",fileName:"Slides.ppt"}]},{number:9,title:"Draft follow-up emails",prompt:"Based on the tone and structure of my example email, please help me draft a follow-up email for my graduate-level entrepreneurial journalism course that reinforces key takeaways and provides clear next steps.",promptQuery:"Based on the tone and structure of my example email, please help me draft a follow-up email for my [course] that reinforces key takeaways and provides clear next steps. [Add criteria or additional direction]\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Syllabus.pdf"},{icon:"slide",fileName:"Slides.ppt"},{icon:"file",fileName:"Example follow-up email.pdf"}]},{number:10,title:"Draft a letter of recommendation",prompt:"I need to write a letter of recommendation for my research assistant. Here are my thoughts about her strengths and accomplishments. Use the structure, style, and voice of my past letters of recommendation to write a first draft.",promptQuery:"I need to write a letter of recommendation for [student]. Here are my thoughts about their strengths and accomplishments. Use the structure, style, and voice of my past letters of recommendation to write a first draft.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Notes on student.txt"},{icon:"file",fileName:"Past letters of recommendation.pdf"}]},{number:11,title:"Draft a grant proposal",prompt:"I’m applying for this grant proposal. Help me figure out how to pitch my research project on translating and collecting the works of Adah Isaacs Menken.",promptQuery:"Help me figure out how to pitch my research project on [topic] for this grant proposal.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"NEH Grant requirements.pdf"}]},{number:12,title:"Conduct a lit review",prompt:"Conduct a comprehensive literature review on consumer-AI relationships that I can use in my class notes for an upcoming lecture. Give me a report from marketing and consumer research journals, drawing exclusively from articles published in the Financial Times Top 50 (FT50) academic journals. Ensure that all references are accurately cited.\n\nYour analysis should encompass:\n• Theoretical Frameworks\n• Conceptual Models\n• Scholarly Debates\n• Research Gaps\n• Methodological Approaches",promptQuery:"Conduct a comprehensive literature review on [topic]. Give me a report from research journals, drawing exclusively from [sources]. Ensure that all references are accurately cited. [Add additional details and direction.]",attachments:[]},{number:13,title:"Write replies to an email",prompt:"Act as my executive communications assistant. Please write a professional, compassionate, and warm response to the following email.\n\nI want to maintain strong relationships, even when I’m setting a boundary, asking for more time, or saying no. Help me sound emotionally intelligent, gracious, and human. When appropriate, affirm their message, acknowledge effort or feelings, and provide helpful next steps or support.\n\nPlease keep it short, sincere, and solution-oriented. Close the message with warmth, encouragement, or gratitude.",promptQuery:"Act as my executive communications assistant. Please write a professional, compassionate, and warm response to the following email. [Add direction on length or goals.]\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Email.pdf"}]},{number:14,title:"AI-proof your assignments ",prompt:"I want to redesign assignments that are easily completed by AI. Can you analyze this assignment and suggest a creative alternative that still assesses the same skills — but makes AI a support tool, not the whole solution?",promptQuery:"I want to redesign assignments that are easily completed by AI. Can you analyze this assignment and suggest a creative alternative that still assesses the same skills — but makes AI a support tool, not the whole solution? \n\n[Upload docs]",attachments:[{icon:"file",fileName:"Syllabus.pdf"},{icon:"file",fileName:"Assignment.pdf"}]},{number:15,title:"Summarize a report",prompt:"I’m giving a presentation to engineering faculty today. Give me a few quick bullets from this report that I could use to encourage the use of gen AI to map to ABET guidelines.",promptQuery:"I’m giving a presentation today. Give me a few quick bullets from this report that I could use to support my argument on [topic].\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Report.pdf"}]},{number:16,title:"Make your examples more relevant",prompt:"This is a problem set question that I would like to update with a new, more relevant example than teleworking during the pandemic. Find a set of data that is about a related topic but more interesting and relevant to 2025, and find a table that I can use instead of this one. Find three options for me to choose from.\n\nProvide links to the relevant sources and provide your output in the same format as the attached screenshot.",promptQuery:"This is a problem set question that I would like to update with a new, more relevant example. Find a set of data that is about a related topic but more interesting and relevant to 2025. Find three options for me to choose from.\n\nProvide links to the relevant sources and provide your output in the same format as the attached screenshot.\n\n[Upload docs]",attachments:[{icon:"image",fileName:"Table.png"},{icon:"image",fileName:"ProblemSet.png"}]},{number:17,title:"Generate makeup exams",prompt:"Drawing from the study guide, please create a makeup exam question in the same style as these two questions. Do not touch on any of the topics mentioned.",promptQuery:"Drawing from the study guide, please create an exam question in the same style as this one. Do not touch on any of the topics mentioned.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"StudyGuide.pdf"},{icon:"file",fileName:"ExamQuestions.pdf"}]},{number:18,title:"Design a more interactive lesson",prompt:"You are a senior instructional design expert. Help me design a short online learning module on project management that fosters active learning and deeper understanding. \n\nFor this part of the lesson: \n- Include 2–4 Socratic questions that spark critical thinking and reflection\n- Suggest 1–2 low-overhead online activities (polls, chats, breakout discussions, role-plays) suitable for a virtual classroom\n- Use a scenario or real-world example related to ground the learning.\n\nMake your answer in an easy-to-follow list or outline format.",promptQuery:"You are a senior instructional design expert. Help me design a short [format] module on [topic] that fosters active learning and deeper understanding. \n\nFor this part of the lesson: \n- Include 2–4 Socratic questions that spark critical thinking and reflection\n- Suggest 1–2 low-overhead activities (polls, chats, breakout discussions, role-plays) suitable for a [virtual or physical] classroom\n- Use a scenario or real-world example related to ground the learning.\n\nMake your answer in an easy-to-follow list or outline format.",attachments:[{icon:"slide",fileName:"Lesson.ppt"}]},{number:19,title:"Adjust your schedule for a new semester",prompt:"Here is the schedule from last spring. This semester, the course begins on Monday, January 12, 2026. Please adjust the dates so that they represent this semester's schedule, ending May 1, 2026, adjusting for weekend breaks.",promptQuery:"Here is the schedule from last term. Please adjust the dates so that they represent this semester's schedule, beginning on [start date] and ending on [end date], adjusting for weekend breaks.",attachments:[{icon:"file",fileName:"Spring2025.pdf"}]},{number:20,title:"Draft an annual assessment report",prompt:"Assume the role of a university administrator. We are going to write an annual program assessment report. I will provide the learning outcomes being assessed, feedback scores, and a sample report to guide your drafting.",promptQuery:"Assume the role of a university administrator. We are going to write an annual program assessment report. I will provide the learning outcomes being assessed, feedback scores, and a sample report to guide your drafting.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Learning outcomes.pdf"},{icon:"file",fileName:"FeedbackScores.pdf"},{icon:"file",fileName:"AnnualReport2024.pdf"}]},{number:21,title:"Extract and compile grades faster",prompt:"I have a coding task that I would like your help with. I want to go through every submission of this Canvas Speedgrader interface and copy the grade breakdown. Here is a screenshot of the interface - grades are in the box on the middle right. I want a script to save each submission's written feedback, and then click the right arrow button to go to the next submission, and do this over and over, eventually getting a CSV of grade breakdowns for each submission. Create a script to do this that I can run in the Chrome console.\n\nLet me know what you need to write it effectively. Assume I know nothing about coding. Walk me through how to find whatever you need or what to copy.",promptQuery:"I have a coding task that I would like your help with. I want to go through every submission of this Canvas Speedgrader interface and copy the grade breakdown. Here is a screenshot of the interface - grades are in the box on the middle right. I want a script to save each submission's written feedback, and then click the right arrow button to go to the next submission, and do this over and over, eventually getting a CSV of grade breakdowns for each submission. Create a script to do this that I can run in the Chrome console.\n\nLet me know what you need to write it effectively. Assume I know nothing about coding. Walk me through how to find whatever you need or what to copy.",attachments:[{icon:"image",fileName:"CanvasInterface.png"}]},{number:22,title:"Create a lecture outline from a podcast",prompt:"You are an expert lecturer and skilled communicator who specializes in transforming conversational material into clear, engaging, and structured educational content. Your role is to analyze a podcast script, extract its key themes, organize the main arguments logically rather than simply presenting them in chronological order, and present them as a concise lecture that is easy to follow, informative, and impactful. The final product should be suitable for delivery as a 20-minute lecture for a university-level advertising class. Begin by producing a clear outline that highlights the main sections and subpoints of the lecture.",promptQuery:"You are an expert lecturer and skilled communicator who specializes in transforming conversational material into clear, engaging, and structured educational content. Your role is to analyze a podcast script, extract its key themes, organize the main arguments logically rather than simply presenting them in chronological order, and present them as a concise lecture that is easy to follow, informative, and impactful. The final product should be suitable for delivery as a [length]-minute lecture for a [topic] class. Begin by producing a clear outline that highlights the main sections and subpoints of the lecture.\n\n[Upload docs]",attachments:[{icon:"file",fileName:"Transcript.txt"}]}]}}],prefooterTitle:"Share your best chat examples with other educators",prefooterCTALabel:"Share a chat",prefooterCTALink:"https://docs.google.com/forms/d/e/1FAIpQLSfv0AyUX1x_OKeUoX7LNJ3uDp7pHDncBaApNCovrIrKZJQAbQ/viewform",footnote:"These chats were sourced from educators in public policy, computer science, journalism, economics and many other fields. Special thanks to the following educators for their contributions: Dan Levy, PhD; Doreen Mayrell, EdD; Helen Crompton PhD; James M. Pitarresi, PhD; Jeanne Beatrix Law, PhD; Jeffrey J. Bussgang, MBA; Jeremy Caplan, MBA, MS; Jérémie Lumbroso, PhD; Lindsey McInerney; Muhsinah Morris, PhD; Nydia Cappas, PsyD, MBA"};function l({headerNavData:t,effectiveLocale:e}){return a.jsx(r,{pageData:i,effectiveLocale:e,audience:"professors",headerNavData:t})}function c({headerNavData:t,effectiveLocale:e}){return a.jsx(l,{headerNavData:t,effectiveLocale:e})}const b=n(function(){const{headerNavData:e,effectiveLocale:o}=s();return a.jsx(c,{headerNavData:e,effectiveLocale:o})}),w={hasRouteMeta:!0,useScrollRestoration:!0},k=()=>{const t="ChatGPT for university educators",e="Discover the best ChatGPT prompts for professors to streamline teaching—refine your syllabus, create quizzes, tailor assignments, draft recommendations, conduct lit reviews, and more.";return[{title:t},{property:"og:site_name",content:"ChatGPT"},{property:"og:type",content:"website"},{property:"og:title",content:t},{property:"og:url",content:window.location.origin+"/use-cases/university-educators"},{name:"description",content:e},{property:"og:description",content:e},{property:"og:image",content:"https://cdn.openai.com/chatgpt/ctf-cdn/professors/share-image-professors.png"},{property:"og:image:width",content:"1200"},{property:"og:image:height",content:"630"},{property:"twitter:card",content:"summary_large_image"}]},I=()=>[{rel:"canonical",href:window.location.origin+"/use-cases/university-educators"}];export{b as default,w as handle,I as links,k as meta};
